import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale, // ✅ เปลี่ยนจาก TimeSeriesScale เป็น TimeScale
  Title,
  Tooltip,
  Legend
} from "chart.js";
import "chartjs-adapter-date-fns"; // ✅ เพิ่ม Date Adapter
import { Chart } from "react-chartjs-2";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";

// ✅ ลงทะเบียน TimeScale และ Candlestick
ChartJS.register(CategoryScale, LinearScale, TimeScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=BTCUSDT&interval=1m&limit=10");
        const data = await res.json();

        const candlesData = data.map(entry => ({
          x: new Date(entry[0]), // ✅ ใช้ Date object
          o: parseFloat(entry[1]), // Open
          h: parseFloat(entry[2]), // High
          l: parseFloat(entry[3]), // Low
          c: parseFloat(entry[4]), // Close
        }));

        setCandles(candlesData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 60000); // ดึงข้อมูลทุก 1 นาที
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h2>Binary Options Trading Chart</h2>
      <Chart
        type="candlestick"
        data={{
          datasets: [
            {
              label: "BTC/USDT Price",
              data: candles,
              borderColor: "blue",
              borderWidth: 2,
            }
          ],
        }}
        options={{
          scales: {
            x: {
              type: "time", // ✅ ใช้ "time" แทน "timeseries"
              time: {
                unit: "minute",
              },
            },
          },
        }}
      />
    </div>
  );
};

export default TradingChart;
