class IQOptionAPI {
    constructor(token) {
      this.token = token;
      this.ws = null;
      this.isConnected = false;
      this.candles = [];
    }
  
    connect() {
      return new Promise((resolve, reject) => {
        this.ws = new WebSocket("wss://iqoption.com/echo/websocket"); // âœ… à¹ƒà¸Šà¹‰ WebSocket à¸‚à¸­à¸‡à¹€à¸šà¸£à¸²à¸§à¹Œà¹€à¸‹à¸­à¸£à¹Œ
  
        this.ws.onopen = () => {
          console.log("âœ… Connected to IQ Option WebSocket");
          this.isConnected = true;
          this.authenticate();
          resolve();
        };
  
        this.ws.onmessage = (message) => {
          const data = JSON.parse(message.data);
          if (data.name === "candles") {
            this.candles = data.msg.candles;
          }
        };
  
        this.ws.onerror = (error) => {
          console.error("âŒ WebSocket Error:", error);
          reject(error);
        };
  
        this.ws.onclose = () => {
          console.log("ðŸ”Œ Disconnected from IQ Option WebSocket");
          this.isConnected = false;
        };
      });
    }
  
    authenticate() {
      const authData = {
        name: "authenticate",
        msg: { token: this.token }
      };
      this.ws.send(JSON.stringify(authData));
    }
  
    subscribeCandles(asset, interval) {
      const subscribeData = {
        name: "subscribeMessage",
        msg: {
          name: "candles",
          version: "1.0",
          params: {
            routingFilters: { active_id: asset, size: interval, only_closed: false }
          }
        }
      };
      this.ws.send(JSON.stringify(subscribeData));
    }
  
    getCandles() {
      return this.candles;
    }
  }
  
  export default IQOptionAPI;
