import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import "chartjs-adapter-date-fns";
import { Chart } from "react-chartjs-2";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";

ChartJS.register(CategoryScale, LinearScale, TimeScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);
  const [signals, setSignals] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=GBPUSDT&interval=1m&limit=20");
        const data = await res.json();

        if (!data || data.length === 0) {
          console.error("❌ No data received from API.");
          return;
        }

        const candlesData = data.map(entry => ({
          x: new Date(parseInt(entry[0])), // ✅ ใช้ `x` เป็นเวลา
          o: parseFloat(entry[1]), // Open
          h: parseFloat(entry[2]), // High
          l: parseFloat(entry[3]), // Low
          c: parseFloat(entry[4]), // Close
        }));

        // ✅ คำนวณ SMA (Simple Moving Average)
        const period = 5; // ใช้ค่าเฉลี่ย 5 แท่งเทียน
        const smaData = candlesData.map((candle, index) => {
          if (index >= period) {
            const avg = candlesData.slice(index - period, index)
              .reduce((sum, c) => sum + c.c, 0) / period;
            return { x: candle.x, sma: avg };
          }
          return { x: candle.x, sma: candle.c }; // ถ้าข้อมูลไม่พอให้ใช้ค่าปิดแทน
        });

        // ✅ AI คำนวณ BUY/SELL
        const signalData = candlesData.map((candle, index) => {
          if (index > period) {
            const sma = smaData[index].sma;
            const prevClose = candlesData[index - 1].c;
            
            if (candle.c > sma && candle.c > prevClose) {
              return { x: candle.x, y: candle.l - 0.0003, type: "BUY" };
            } else if (candle.c < sma && candle.c < prevClose) {
              return { x: candle.x, y: candle.h + 0.0003, type: "SELL" };
            }
          }
          return null;
        }).filter(s => s !== null);

        console.log("✅ Updated Candle Data:", candlesData);
        console.log("✅ AI Signals:", signalData);

        setCandles(candlesData);
        setSignals(signalData);
      } catch (error) {
        console.error("❌ Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      backgroundColor: "#000",
      padding: "10px",
      color: "#0f0",
      textAlign: "center",
      width: "390px", // ✅ ปรับขนาดให้เหมือน MT4
      height: "320px",
      border: "3px solid #0f0",
      borderRadius: "10px",
    }}>
      <h3 style={{ fontSize: "20px", color: "#0f0", margin: "5px 0" }}>📊 GBP/USD (AI Trading)</h3>
      <Chart
        type="candlestick"
        data={{
          datasets: [
            {
              label: "GBP/USD Price",
              data: candles,
              borderColor: "#fff",
              borderWidth: 1,
              color: {
                up: "green",
                down: "red",
                unchanged: "gray",
              },
              parsing: false,
            },
            {
              label: "Signals",
              data: signals.map(signal => ({
                x: signal.x,
                y: signal.y,
              })),
              pointStyle: "triangle",
              pointRadius: 8, // ✅ ปรับขนาดลูกศรให้เล็กลง
              backgroundColor: signals.map(signal => (signal.type === "BUY" ? "green" : "red")),
            }
          ],
        }}
        options={{
          responsive: false,
          maintainAspectRatio: false,
          scales: {
            x: {
              type: "time",
              time: {
                unit: "minute",
                tooltipFormat: "HH:mm",
              },
              ticks: { color: "#fff", font: { size: 12 } },
            },
            y: {
              ticks: { color: "#fff", font: { size: 12 } },
              min: Math.min(...candles.map(c => c.l)) - 0.001,
              max: Math.max(...candles.map(c => c.h)) + 0.001,
            }
          },
          plugins: {
            legend: { display: false },
          }
        }}
        width={390}
        height={280}
      />
    </div>
  );
};

export default TradingChart;
