import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import "chartjs-adapter-date-fns"; 
import { Chart } from "react-chartjs-2";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";

ChartJS.register(CategoryScale, LinearScale, TimeScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=GBPUSDT&interval=1m&limit=10");
        const data = await res.json();

        if (!data || data.length === 0) {
          console.error("‚ùå No data received from API.");
          return;
        }

        const candlesData = data.map(entry => ({
          t: new Date(parseInt(entry[0])), // ‚úÖ ‡πÉ‡∏ä‡πâ `t` (Timestamp) ‡∏ï‡∏≤‡∏°‡∏°‡∏≤‡∏ï‡∏£‡∏ê‡∏≤‡∏ô Chart.js
          o: Number(entry[1]), // ‚úÖ ‡πÅ‡∏õ‡∏•‡∏á‡∏Ñ‡πà‡∏≤‡πÄ‡∏õ‡πá‡∏ô `Number`
          h: Number(entry[2]),
          l: Number(entry[3]),
          c: Number(entry[4]),
        }));

        console.log("‚úÖ Candle Data Processed:", candlesData); // ‚úÖ Debugging Data
        setCandles(candlesData);
      } catch (error) {
        console.error("‚ùå Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 60000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ backgroundColor: "#1e1e1e", padding: "10px", color: "#fff", textAlign: "center" }}>
      <h2>üìä GBP/USD 1-Min Candlestick Chart</h2>
      {candles.length > 0 ? (
        <Chart
          type="candlestick"
          data={{
            datasets: [
              {
                label: "GBP/USD Price",
                data: candles,
                borderColor: "#fff",
                borderWidth: 1,
                backgroundColor: candles.map(c => (c.c > c.o ? "green" : "red")), // ‚úÖ ‡∏™‡∏µ‡πÄ‡∏Ç‡∏µ‡∏¢‡∏ß‡πÅ‡∏ó‡πà‡∏á‡∏Ç‡∏∂‡πâ‡∏ô ‡∏™‡∏µ‡πÅ‡∏î‡∏á‡πÅ‡∏ó‡πà‡∏á‡∏•‡∏á
              }
            ],
          }}
          options={{
            scales: {
              x: {
                type: "time",
                time: {
                  unit: "minute",
                  tooltipFormat: "yyyy-MM-dd HH:mm:ss",
                },
                ticks: { color: "#fff" },
              },
              y: {
                ticks: { color: "#fff" },
              }
            },
            plugins: {
              legend: {
                labels: { color: "#fff" }
              }
            }
          }}
        />
      ) : (
        <p>‚è≥ ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•...</p>
      )}
    </div>
  );
};

export default TradingChart;
