import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import "chartjs-adapter-date-fns";
import { Chart } from "react-chartjs-2";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";

ChartJS.register(CategoryScale, LinearScale, TimeScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=GBPUSDT&interval=1m&limit=20");
        const data = await res.json();

        if (!data || data.length === 0) {
          console.error("âŒ No data received from API.");
          return;
        }

        const candlesData = data.map(entry => ({
          x: new Date(parseInt(entry[0])), // âœ… à¹ƒà¸Šà¹‰ `x` à¸ªà¸³à¸«à¸£à¸±à¸š Chart.js
          o: parseFloat(entry[1]), // Open
          h: parseFloat(entry[2]), // High
          l: parseFloat(entry[3]), // Low
          c: parseFloat(entry[4]), // Close
        }));

        console.log("âœ… Updated Candle Data:", candlesData);
        setCandles(candlesData);
      } catch (error) {
        console.error("âŒ Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000); // âœ… à¸­à¸±à¸›à¹€à¸”à¸•à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸—à¸¸à¸ 5 à¸§à¸´à¸™à¸²à¸—à¸µ (à¹à¸—à¸™ 1 à¸™à¸²à¸—à¸µ)
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      backgroundColor: "#000",
      padding: "10px",
      color: "#0f0",
      textAlign: "center",
      width: "300px",
      height: "250px",
      border: "2px solid #0f0",
      borderRadius: "10px",
    }}>
      <h3 style={{ fontSize: "16px", margin: "5px 0" }}>ðŸ“Š GBP/USD (Real-time)</h3>
      <Chart
        type="candlestick"
        data={{
          datasets: [
            {
              label: "GBP/USD Price",
              data: candles,
              borderColor: "#fff",
              borderWidth: 1,
              color: {
                up: "green",
                down: "red",
                unchanged: "gray",
              },
              parsing: false,
            }
          ],
        }}
        options={{
          responsive: false,
          maintainAspectRatio: false,
          scales: {
            x: {
              type: "time",
              time: {
                unit: "minute",
                tooltipFormat: "HH:mm",
              },
              ticks: { color: "#fff", font: { size: 10 } },
            },
            y: {
              ticks: { color: "#fff", font: { size: 10 } },
              min: Math.min(...candles.map(c => c.l)) - 0.001, // âœ… à¸›à¸£à¸±à¸šà¸‚à¸­à¸šà¸¥à¹ˆà¸²à¸‡à¸‚à¸­à¸‡à¸à¸£à¸²à¸Ÿ
              max: Math.max(...candles.map(c => c.h)) + 0.001, // âœ… à¸›à¸£à¸±à¸šà¸‚à¸­à¸šà¸šà¸™à¸‚à¸­à¸‡à¸à¸£à¸²à¸Ÿ
            }
          },
          plugins: {
            legend: { display: false },
          }
        }}
        width={300}
        height={200}
      />
    </div>
  );
};

export default TradingChart;
