import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import "chartjs-adapter-date-fns";
import { Chart } from "react-chartjs-2";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";

ChartJS.register(CategoryScale, LinearScale, TimeScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);
  const [signals, setSignals] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=GBPUSDT&interval=1m&limit=20");
        const data = await res.json();

        if (!data || data.length === 0) {
          console.error("âŒ No data received from API.");
          return;
        }

        const candlesData = data.map(entry => ({
          x: new Date(parseInt(entry[0])), // âœ… à¹ƒà¸Šà¹‰ `x` à¹€à¸›à¹‡à¸™à¹€à¸§à¸¥à¸²
          o: parseFloat(entry[1]), // Open
          h: parseFloat(entry[2]), // High
          l: parseFloat(entry[3]), // Low
          c: parseFloat(entry[4]), // Close
        }));

        // âœ… à¸ªà¸£à¹‰à¸²à¸‡à¸ªà¸±à¸à¸à¸²à¸“ BUY/SELL à¸­à¸±à¸•à¹‚à¸™à¸¡à¸±à¸•à¸´
        const signalData = candlesData.map((candle, index) => {
          if (index > 0) {
            const prevCandle = candlesData[index - 1];
            if (candle.o < prevCandle.l) {
              return { x: candle.x, y: candle.l - 0.0003, type: "BUY" };
            } else if (candle.o > prevCandle.h) {
              return { x: candle.x, y: candle.h + 0.0003, type: "SELL" };
            }
          }
          return null;
        }).filter(s => s !== null);

        console.log("âœ… Updated Candle Data:", candlesData);
        console.log("âœ… Signals:", signalData);

        setCandles(candlesData);
        setSignals(signalData);
      } catch (error) {
        console.error("âŒ Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      backgroundColor: "#000",
      padding: "10px",
      color: "#0f0",
      textAlign: "center",
      width: "390px", // âœ… à¸›à¸£à¸±à¸šà¸‚à¸™à¸²à¸”à¹ƒà¸«à¹‰à¹€à¸«à¸¡à¸·à¸­à¸™ MT4
      height: "320px",
      border: "3px solid #0f0",
      borderRadius: "10px",
    }}>
      <h3 style={{ fontSize: "20px", color: "#0f0", margin: "5px 0" }}>ðŸ“Š GBP/USD</h3>
      <Chart
        type="candlestick"
        data={{
          datasets: [
            {
              label: "GBP/USD Price",
              data: candles,
              borderColor: "#fff",
              borderWidth: 1,
              color: {
                up: "green", // âœ… à¸ªà¸µà¹à¸—à¹ˆà¸‡à¸‚à¸¶à¹‰à¸™
                down: "red", // âœ… à¸ªà¸µà¹à¸—à¹ˆà¸‡à¸¥à¸‡
                unchanged: "gray",
              },
              parsing: false,
            },
            {
              label: "Signals",
              data: signals.map(signal => ({
                x: signal.x,
                y: signal.y,
              })),
              pointStyle: "triangle",
              pointRadius: 8, // âœ… à¸›à¸£à¸±à¸šà¸‚à¸™à¸²à¸”à¸¥à¸¹à¸à¸¨à¸£à¹ƒà¸«à¹‰à¹€à¸¥à¹‡à¸à¸¥à¸‡
              backgroundColor: signals.map(signal => (signal.type === "BUY" ? "green" : "red")),
            }
          ],
        }}
        options={{
          responsive: false,
          maintainAspectRatio: false,
          scales: {
            x: {
              type: "time",
              time: {
                unit: "minute",
                tooltipFormat: "HH:mm",
              },
              ticks: { color: "#fff", font: { size: 12 } },
            },
            y: {
              ticks: { color: "#fff", font: { size: 12 } },
              min: Math.min(...candles.map(c => c.l)) - 0.001,
              max: Math.max(...candles.map(c => c.h)) + 0.001,
            }
          },
          plugins: {
            legend: { display: false },
          }
        }}
        width={390}
        height={280}
      />
    </div>
  );
};

export default TradingChart;
