import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import "chartjs-adapter-date-fns"; 
import { Chart } from "react-chartjs-2";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";

ChartJS.register(CategoryScale, LinearScale, TimeScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=GBPUSDT&interval=1m&limit=20");
        const data = await res.json();

        const candlesData = data.map(entry => ({
          t: new Date(parseInt(entry[0])), // ✅ ใช้ `t` แทน `x` สำหรับเวลา
          o: parseFloat(entry[1]), // Open
          h: parseFloat(entry[2]), // High
          l: parseFloat(entry[3]), // Low
          c: parseFloat(entry[4]), // Close
        }));

        setCandles(candlesData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 60000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ backgroundColor: "#1e1e1e", padding: "10px", color: "#fff", textAlign: "center" }}>
      <h2>GBP/USD 1-Min Candlestick Chart</h2>
      <Chart
        type="candlestick"
        data={{
          datasets: [
            {
              label: "GBP/USD Price",
              data: candles,
              borderColor: "#fff",
              borderWidth: 1,
              backgroundColor: "#000",
            }
          ],
        }}
        options={{
          scales: {
            x: {
              type: "time",
              time: {
                unit: "minute",
                tooltipFormat: "yyyy-MM-dd HH:mm:ss",
              },
              ticks: { color: "#fff" },
            },
            y: {
              ticks: { color: "#fff" },
            }
          },
          plugins: {
            legend: {
              labels: { color: "#fff" }
            }
          }
        }}
      />
    </div>
  );
};

export default TradingChart;
