import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import "chartjs-adapter-date-fns";
import { Chart } from "react-chartjs-2";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";

// âœ… à¸¥à¸‡à¸—à¸°à¹€à¸šà¸µà¸¢à¸™ Chart.js
ChartJS.register(CategoryScale, LinearScale, TimeScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);
  const [signals, setSignals] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=GBPUSDT&interval=1m&limit=20");
        const data = await res.json();

        if (!data || data.length === 0) {
          console.error("âŒ No data received from API.");
          return;
        }

        const candlesData = data.map(entry => ({
          x: new Date(parseInt(entry[0])), // âœ… à¸•à¹‰à¸­à¸‡à¹ƒà¸Šà¹‰ `x` à¹ƒà¸™ Chart.js
          o: parseFloat(entry[1]), // Open
          h: parseFloat(entry[2]), // High
          l: parseFloat(entry[3]), // Low
          c: parseFloat(entry[4]), // Close
        }));

        // âœ… à¸ªà¸£à¹‰à¸²à¸‡à¸ªà¸±à¸à¸à¸²à¸“ BUY/SELL à¹à¸šà¸šà¸ªà¸¸à¹ˆà¸¡ (à¸„à¸¸à¸“à¸ªà¸²à¸¡à¸²à¸£à¸–à¹ƒà¸Šà¹‰ AI Model à¹„à¸”à¹‰)
        const signalData = candlesData.map((candle, index) => ({
          x: candle.x,
          y: candle.c,
          type: Math.random() > 0.5 ? "BUY" : "SELL",
        }));

        console.log("âœ… Processed Candle Data:", candlesData);
        console.log("âœ… Signals Data:", signalData);

        setCandles(candlesData);
        setSignals(signalData);
      } catch (error) {
        console.error("âŒ Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 60000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ backgroundColor: "#000", padding: "10px", color: "#0f0", textAlign: "center" }}>
      <h2>ðŸ“Š GBP/USD 1-Min Candlestick Chart</h2>
      <Chart
        type="candlestick"
        data={{
          datasets: [
            {
              label: "GBP/USD Price",
              data: candles,
              borderColor: "#fff",
              borderWidth: 1,
              color: {
                up: "green", // âœ… à¸ªà¸µà¹à¸—à¹ˆà¸‡à¸‚à¸¶à¹‰à¸™
                down: "red", // âœ… à¸ªà¸µà¹à¸—à¹ˆà¸‡à¸¥à¸‡
                unchanged: "gray",
              },
              parsing: false,
            },
            {
              label: "Signals",
              data: signals.map(signal => ({
                x: signal.x,
                y: signal.type === "BUY" ? signal.y - 0.0005 : signal.y + 0.0005,
              })),
              pointStyle: "triangle",
              pointRadius: 10,
              backgroundColor: signals.map(signal => (signal.type === "BUY" ? "green" : "red")),
            }
          ],
        }}
        options={{
          scales: {
            x: {
              type: "time",
              time: {
                unit: "minute",
                tooltipFormat: "yyyy-MM-dd HH:mm:ss",
              },
              ticks: { color: "#fff" },
            },
            y: {
              ticks: { color: "#fff" },
            }
          },
          plugins: {
            legend: {
              labels: { color: "#fff" }
            }
          }
        }}
      />
    </div>
  );
};

export default TradingChart;
