import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import "chartjs-adapter-date-fns"; 
import { Chart } from "react-chartjs-2";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";

// ✅ ลงทะเบียน TimeScale และ Candlestick
ChartJS.register(CategoryScale, LinearScale, TimeScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // ✅ ใช้ Binance API ดึงข้อมูล GBP/USD แบบ 1 นาที
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=GBPUSDT&interval=1m&limit=50");
        const data = await res.json();

        const candlesData = data.map(entry => ({
          x: new Date(entry[0]), // ✅ ใช้ Date object สำหรับ Time Scale
          o: parseFloat(entry[1]), // Open
          h: parseFloat(entry[2]), // High
          l: parseFloat(entry[3]), // Low
          c: parseFloat(entry[4]), // Close
        }));

        setCandles(candlesData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 60000); // ✅ อัปเดตทุก 1 นาที
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h2>GBP/USD 1-Min Candlestick Chart</h2>
      {candles.length > 0 ? (
        <Chart
          type="candlestick"
          data={{
            datasets: [
              {
                label: "GBP/USD Price",
                data: candles,
                borderColor: "blue",
                borderWidth: 2,
              }
            ],
          }}
          options={{
            scales: {
              x: {
                type: "time", // ✅ กำหนดให้เป็น Time Scale
                time: {
                  unit: "minute",
                  tooltipFormat: "yyyy-MM-dd HH:mm:ss",
                },
              },
            },
          }}
        />
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
};

export default TradingChart;
