import React, { useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import { CandlestickController, CandlestickElement } from "chartjs-chart-financial";
import { Chart } from "react-chartjs-2";

// ลงทะเบียน Candlestick Controller ให้ Chart.js รู้จัก
ChartJS.register(CategoryScale, LinearScale, CandlestickController, CandlestickElement, Title, Tooltip, Legend);

const TradingChart = () => {
  const [candles, setCandles] = useState([]);
  const [timestamps, setTimestamps] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("https://api.binance.com/api/v3/klines?symbol=EURJPY&interval=1m&limit=10");
        const data = await res.json();

        const candlesData = data.map(entry => ({
          x: new Date(entry[0]),
          o: parseFloat(entry[1]), // Open
          h: parseFloat(entry[2]), // High
          l: parseFloat(entry[3]), // Low
          c: parseFloat(entry[4]), // Close
        }));

        setCandles(candlesData);
        setTimestamps(candlesData.map(c => c.x.toLocaleTimeString()));
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 60000); // ดึงข้อมูลทุก 1 นาที
    return () => clearInterval(interval);
  }, []);

  const chartData = {
    labels: timestamps,
    datasets: [
      {
        label: "EUR/JPY Price",
        data: candles,
        borderColor: "blue",
        borderWidth: 2,
      }
    ],
  };

  return (
    <div>
      <h2>Binary Options Trading Chart</h2>
      <Chart type="candlestick" data={chartData} />
    </div>
  );
};

export default TradingChart;
